import React from 'react';
import './App.css';
import { Button, ListGroup, ListGroupItem} from 'react-bootstrap';

var placeholder = document.createElement("li");
placeholder.className = "placeholder";

class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      available:[{id:'startTime',name:'Start Time'},{id:'stopTime',name:'Stop Time'},{id:'perPoint',name:'Per Point'},{id:'initialMargin',name:'Initial Margin'}],
      visible:['change%'],
      fixed:['Symbol & Description']
    }
    this.dragStart=this.dragStart.bind(this);
    this.dragOver=this.dragOver.bind(this);
    this.dragEnd=this.dragEnd.bind(this);
  }

  dragStart(e) {
    this.dragged = e.currentTarget;
    e.dataTransfer.effectAllowed = 'move';
    e.dataTransfer.setData('text/plain', this.dragged);
  }

  dragOver(e) {
    e.preventDefault();
    this.dragged.style.display = "none";
    if(e.target.className === 'placeholder') return;
    this.over = e.target;
    e.target.parentNode.insertBefore(placeholder, e.target);
  }

  dragEnd(e) {
    e.currentTarget.style.display = 'block';
    //check if the new position is under current node
    if(e.currentTarget.parentNode.isSameNode(placeholder.parentNode)){
      e.currentTarget.parentNode.removeChild(placeholder);
    }
    else{
      placeholder.parentNode.removeChild(placeholder);
    }  
    var data = this.state.available;
    var from = Number(this.dragged.dataset.id);
    var to = Number(this.over.dataset.id);
    if(from < to) to--;
    data.splice(to, 0, data.splice(from, 1)[0]);
    this.setState({available: data});
  }

  render(){
    var visible=this.state.visible.map((item,i)=>{
      return(
        <ListGroupItem data-id={i} key={i} draggable='true' onDragStart={this.dragStart} onDragEnd={this.dragEnd}>{item}</ListGroupItem>
      )
    })
    var available=this.state.available.map((item,i) =>{
      return(
        <ListGroupItem data-id={i} key={i} draggable='true' onDragStart={this.dragStart} onDragEnd={this.dragEnd}>{item.name}</ListGroupItem>
      )
    })
    var fixed=this.state.fixed.map((item,i)=>{
      return(
        <ListGroupItem data-id={i} key={i} draggable='true'disabled>{item}</ListGroupItem>
      )
    })
    return(
      <div>
        <h2>Configure Data Fields</h2>
        <span>Drag & drop between columns to configure visible data.</span>
        <ListGroup horizontal>       
            <ListGroup variant="flush" onDragOver={this.dragOver}>              
              <ListGroupItem>{available}</ListGroupItem>
            </ListGroup>
            <ListGroup variant="flush">
              <ListGroupItem onDragOver={this.dragOver}>{fixed} {visible}</ListGroupItem>
            </ListGroup>
        </ListGroup>
        <Button size="sm" variant="primary">Save</Button>
        <Button size="sm" variant="secondary">Cancel</Button>
    </div>      
    )
  }
}

export default App;
