import React from 'react';
import './App.css';
import { Button, ListGroup, ListGroupItem} from 'react-bootstrap';

class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      available:[{id:'startTime',name:'Start Time'},{id:'stopTime',name:'Stop Time'},{id:'perPoint',name:'Per Point'},{id:'initialMargin',name:'Initial Margin'}],
      visible:['change%',],
      fixed:['Symbol & Description']
    }
  }
  dragStart(e) {
    this.dragged = e.currentTarget;
    e.dataTransfer.effectAllowed = 'move';
    e.dataTransfer.setData('text/html', this.dragged);
  }
  render(){
    var visible=this.state.visible.map(item=>{
      return(
        <ListGroupItem data-id={item.id} draggable='true'>{item}</ListGroupItem>
      )
    })
    var available=this.state.available.map(item =>{
      return(
        <ListGroupItem data-id={item.id} draggable='true'>{item.name}</ListGroupItem>
      )
    })
    var fixed=this.state.fixed.map(item=>{
      return(
        <ListGroupItem data-id={item.id} draggable='true'disabled>{item}</ListGroupItem>
      )
    })
    return(
      <div>
        <h2>Configure Data Fields</h2>
        <span>Drag & drop between columns to configure visible data.</span>
        <ListGroup horizontal>       
            <ListGroup variant="flush">              
              <ListGroupItem>{available}</ListGroupItem>
            </ListGroup>
            <ListGroup variant="flush">
              <ListGroupItem>{fixed} {visible}</ListGroupItem>
            </ListGroup>
        </ListGroup>
        <Button size="sm" variant="primary">Save</Button>
        <Button size="sm" variant="secondary">Cancel</Button>
    </div>      
    )
  }
}

export default App;
