import React from 'react';
import './App.css';
import { Button, ListGroup, ListGroupItem} from 'react-bootstrap';

class List extends React.Component{
  constructor(props){
    super(props);
    this.state={...props};
    // this.state={
    //   available:[{id:'startTime',name:'Start Time'},{id:'stopTime',name:'Stop Time'},{id:'perPoint',name:'Per Point'},{id:'initialMargin',name:'Initial Margin'}]
    // }
  }
  render(){
    var visible=this.state.visible.map(item=>{
      return(
        <ListGroupItem data-id={item.id} draggable='true'>{item.name}</ListGroupItem>
      )
    })
    var available=this.state.available.map(item =>{
      return(
        <ListGroupItem data-id={item.id} draggable='true'>{item.name}</ListGroupItem>
      )
    })
    var fixed=this.state.fixed.map(item=>{
      return(
        <ListGroupItem data-id={item.id} draggable='true'>{item.name}</ListGroupItem>
      )
    })
    return(
      <ListGroup>
        {available}
        {visible}
      </ListGroup>
    )
  }
}

class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      available:[{id:'startTime',name:'Start Time'},{id:'stopTime',name:'Stop Time'},{id:'perPoint',name:'Per Point'},{id:'initialMargin',name:'Initial Margin'}],
      visible:[{id:'change%',name:'Change %'},{id:'change',name:'Change'}],
      fixed:[{id:'symbol&description',name:'Symbol & Description'}]
    }
  }
  render(){
    return(
      <div>
        <h2>Configure Data Fields</h2>
        <span>Drag & drop between columns to condigure visible data.</span>
        <ListGroup horizontal>       
            <ListGroup variant="flush">              
              <List available={this.state.available}/>
            </ListGroup>
            <ListGroup variant="flush">
              <List fixed={this.state.fixed} visible={this.state.visible}/>
            </ListGroup>
        </ListGroup>
        <Button size="sm" variant="primary">Save</Button>
        <Button size="sm" variant="secondary">Cancel</Button>
    </div>      
    )
  }
}

export default App;
