import React from 'react';
import './App.css';
import { Button, ListGroup, ListGroupItem, Card} from 'react-bootstrap';
class App extends React.Component{
  constructor(props){
    super(props);
    this.state={
      available:[],
      visible:[],
      fixed:[]
    }
  }
  render(){
    return(
      <div>
        <h2>Configure Data Fields</h2>
        <span>Drag & drop between columns to condigure visible data.</span>
        <Card style={{ width: '50%' }}>
            <ListGroup variant="flush">
              <p>Availble</p>
              <ListGroup.Item>Start Time</ListGroup.Item>
              <ListGroup.Item>Stop Time</ListGroup.Item>
              <ListGroup.Item>Per Point</ListGroup.Item>
              <ListGroup.Item>Initial Margin</ListGroup.Item>
            </ListGroup>
        </Card>
        <Card style={{ width: '50%' }}>    
            <ListGroup variant="flush">
              <p>Visible</p>
              <ListGroupItem disabled>Symbol & Description</ListGroupItem>
              <ListGroup.Item>Change %</ListGroup.Item>
              <ListGroup.Item>Change</ListGroup.Item>
              <ListGroup.Item>Last</ListGroup.Item>
              <ListGroup.Item>Last Volume</ListGroup.Item>
            </ListGroup>
        </Card>
        <Button size="sm" variant="primary">Save</Button>
        <Button size="sm" variant="secondary">Cancel</Button>
    </div>      
    )
  }
}

export default App;
