import React, { useState } from "react";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";
import { Button } from 'react-bootstrap';
import { BsList } from 'react-icons/bs';

const available = [
  { id: "startTime", name: "Start Time" },
  { id: "stopTime", name: "Stop Time" },
  { id: "perPoint", name: "Per Point" },
  { id: "initialMargin" , name: "Initial Margin" },
];

const visible = [
  { id: "change%", name: "Change %"},
  { id: "change", name: "Change"},
  { id: "last", name: "Last" },
  { id: "lastVolume", name: "Last Volume" },
];

const fixed = [
  { id: "symbol&Description", name: "Symbol & Description"},
];

const columnsFromBackend = {
  "available": {
    name: "Available",
    items: available
  },
  "visible": {
    name: "Visible",
    items: fixed.concat(visible)
  },
};

const onDoubleClick=(event,fix,setFixed)=>{
  console.log(event.target);
  const name = event.target.firstChild;
  const copied=[...fixed]
  const id = name;
  setFixed({
    ...fix,
    fix: {...id,},
  });
  window.alert(fix);
  console.log(fix);
}

const onDragEnd = (result, columns, setColumns) => {
  if (!result.destination) return;
  const { source, destination } = result;

  if (source.droppableId !== destination.droppableId) {
    const sourceColumn = columns[source.droppableId];
    const destColumn = columns[destination.droppableId];
    const sourceItems = [...sourceColumn.items];
    const destItems = [...destColumn.items];
    const [removed] = sourceItems.splice(source.index, 1);
    destItems.splice(destination.index, 0, removed);
    setColumns({
      ...columns,
      [source.droppableId]: {
        ...sourceColumn,
        items: sourceItems
      },
      [destination.droppableId]: {
        ...destColumn,
        items: destItems
      }
    });
  } else {
    const column = columns[source.droppableId];
    const copiedItems = [...column.items];
    const [removed] = copiedItems.splice(source.index, 1);
    copiedItems.splice(destination.index, 0, removed);
    setColumns({
      ...columns,
      [source.droppableId]: {
        ...column,
        items: copiedItems
      }
    });
  }
};

function App() {
  const [columns, setColumns] = useState(columnsFromBackend);
  const [fix,setFixed] = useState(fixed);
  return (
    <div>
      <div style={{ background:"#000000", display:"flex", height: "100%", color:"#ffffff", paddingTop:"20px", paddingLeft:"20px"}}>
        <h5>Configure Data Fields</h5>
      </div>
      <div style={{ background:"#000000", display:"flex", height: "100%", color:"#9da4ab",paddingLeft:"20px"}}>
        <span>Drag & Drop between columns to configure visible data.</span>
      </div>
      <div style={{ background:"#000000",display: "flex", height: "100%", padding:"20px" }}>
        <DragDropContext style={{color:"white"}} onDragEnd={result => onDragEnd(result, columns, setColumns)}>
          {Object.entries(columns).map(([columnId, column], index) => {
            return (   
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                }}
                key={columnId}
                onDoubleClick={event=>onDoubleClick(event,fix,setFixed)}
              >
                <p style={{color:'#9da4ab',paddingLeft:"15px",marginTop:"20px"}}>{column.name}</p>
                <div>
                  <Droppable droppableId={columnId} key={columnId}>
                    {(provided) => {
                      return (
                        <div
                          {...provided.droppableProps}
                          ref={provided.innerRef}
                          style={{
                            background: "#000000",
                            padding: 3,
                            width: 300,
                            borderStyle:"none none none dashed",
                            borderWidth:"3px",
                          }}
                        >
                          {column.items.map((item, index) => {
                            return (
                              <Draggable
                                key={item.id}
                                draggableId={item.id}
                                index={index}
                              >
                                {(provided, snapshot) => {
                                  return (
                                    <div
                                      ref={provided.innerRef}
                                      {...provided.draggableProps}
                                      {...provided.dragHandleProps}
                                      style={{
                                        userSelect: "none",
                                        padding: 5,
                                        margin: "0 0 5px 5px",
                                        minHeight: "20px",
                                        backgroundColor: snapshot.isDragging
                                          ? "#252930"
                                          : "#000000",
                                        color: "white",
                                        ...provided.draggableProps.style
                                      }}
                                    >
                                      <div className={"iconWrapper"}>
                                        <div style={{padding:"10px",float:"left"}}>
                                          <BsList/>
                                        </div>
                                        <div style={{padding:"12px"}}>
                                          {item.name}
                                        </div>
                                      </div>
                                    </div>
                                  );
                                }}
                              </Draggable>
                            );
                          })}
                          {provided.placeholder}
                        </div>
                      );
                    }}
                  </Droppable>
                </div>
              </div>
            );
          })}
        </DragDropContext>
      </div>
      <div style={{background:"#000000" , height:"1000px", padding:"20px"}}>
        <Button style={{margin:"10px", width:"90px"}} variant="info">Save</Button>
        <Button style={{margin:"10px", width:"90px"}} variant="secondary">Cancel</Button>
      </div>

    </div>
  );
}

export default App;
