import React, { useState } from "react";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd";

const available = [
  { id: "startTime", name: "Start Time" },
  { id: "stopTime", name: "Stop Time" },
  { id: "perPoint", name: "Per Point" },
  { id: "initialMargin" , name: "Initial Margin" },
];

const visible = [
  { id: "change%", name: "Change %" },
  { id: "change", name: "Change" },
  { id: "last", name: "Last" },
  { id: "lastVolume", name: "Last Volume" },
];

const fixed = [
  { id: "symbol&Description", name: "Symbol & Description" },
];

const columnsFromBackend = {
  "available": {
    name: "Available",
    items: available
  },
  "visible": {
    name: "Visible",
    items: fixed.concat(visible)
  },
};

const onDoubleClick=(result)=>{

}


const onDragEnd = (result, columns, setColumns) => {
  if (!result.destination) return;
  const { source, destination } = result;

  if (source.droppableId !== destination.droppableId) {
    const sourceColumn = columns[source.droppableId];
    const destColumn = columns[destination.droppableId];
    const sourceItems = [...sourceColumn.items];
    const destItems = [...destColumn.items];
    const [removed] = sourceItems.splice(source.index, 1);
    destItems.splice(destination.index, 0, removed);
    setColumns({
      ...columns,
      [source.droppableId]: {
        ...sourceColumn,
        items: sourceItems
      },
      [destination.droppableId]: {
        ...destColumn,
        items: destItems
      }
    });
  } else {
    const column = columns[source.droppableId];
    const copiedItems = [...column.items];
    const [removed] = copiedItems.splice(source.index, 1);
    copiedItems.splice(destination.index, 0, removed);
    setColumns({
      ...columns,
      [source.droppableId]: {
        ...column,
        items: copiedItems
      }
    });
  }
};

function App() {
  const [columns, setColumns] = useState(columnsFromBackend);
  return (
    <div style={{ background:"#0f1117",display: "flex", justifyContent: "center", height: "100%" }}>
      <div style={{color:"white", display:"absolute"}}>
        <h5>Configure Data Fields</h5>
        <h6>Drag & Drop between columns to configure visible data.</h6>

      </div>
      <DragDropContext style={{color:"white"}} onDragEnd={result => onDragEnd(result, columns, setColumns)} onDoubleClick={result=>onDoubleClick(result)}>
        {Object.entries(columns).map(([columnId, column], index) => {
          return (   
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
              }}
              key={columnId}
            >
              <p style={{color:'#393e45',}}>{column.name}</p>
              <div>
                <Droppable droppableId={columnId} key={columnId}>
                  {(provided) => {
                    return (
                      <div
                        {...provided.droppableProps}
                        ref={provided.innerRef}
                        style={{
                          background: "#0f1117",
                          padding: 4,
                          width: 250,
                          minHeight: 1000,
                          borderStyle:"none dashed none none",
                        }}
                      >
                        {column.items.map((item, index) => {
                          return (
                            <Draggable
                              key={item.id}
                              draggableId={item.id}
                              index={index}
                            >
                              {(provided, snapshot) => {
                                return (
                                  <div
                                    ref={provided.innerRef}
                                    {...provided.draggableProps}
                                    {...provided.dragHandleProps}
                                    style={{
                                      userSelect: "none",
                                      padding: 16,
                                      margin: "0 0 8px 0",
                                      minHeight: "50px",
                                      backgroundColor: snapshot.isDragging
                                        ? "#252930"
                                        : "#0f1117",
                                      color: "white",
                                      ...provided.draggableProps.style
                                    }}
                                  >
                                    {item.name}
                                  </div>
                                );
                              }}
                            </Draggable>
                          );
                        })}
                        {provided.placeholder}
                      </div>
                    );
                  }}
                </Droppable>
              </div>
            </div>
          );
        })}
      </DragDropContext>
    </div>
  );
}

export default App;
